mapper.select:
SELECT

FROM t_statistics a

WHERE a.id = #{id}

SELECT ROUND(SUM(office_time)/60,2) workHour,a.username "create_by"
 FROM t_statistics a
 WHERE
 1=1 AND DATE_FORMAT(a.update_time,'%Y-%m-%d') = #{date}
 AND a.attendance_group = #{attendanceGroup}
 GROUP BY a.create_by

SELECT round(SUM(office_time)/60,2) workHour,a.username "create_by"
FROM t_statistics a
WHERE
1=1 and DATE_FORMAT(a.update_time,'%Y-%m') = #{date}
AND a.attendance_group = #{attendanceGroup}
GROUP BY a.create_by

SELECT SUM(late_time) lateTime,a.username "create_by"
FROM t_statistics a
WHERE
late_time IS NOT NULL and DATE_FORMAT(a.update_time,'%Y-%m') = #{date}
AND a.attendance_group = #{attendanceGroup}
GROUP BY a.create_by

SELECT

FROM t_statistics a
WHERE a.create_by=#{createBy} AND DATE(a.create_time)=#{createTime}
